sprites.script:
this.onStart = function() {

	this.uiTextSize = 64
	this.uiText = ""
	this.text = "0"

	this.set = function(text) {
		this.text = text
		this.uiText = text
	}

}



this.onUpdate = function(sprites) {
	if (sprites["play"].run) {
		this.uiText = this.text
	} else {
		this.uiText = ""
	}
}

this.onStart = function() {

	this.run = false
	this.diePlayed = false

}


this.onClick = function() {
this.x = -500
sprites["bird"].x = 55
sprites["bird"].y = 270
sprites["pipe"].show()
sprites["score"].set("0")
sprites["score_hitbox"].used = false
sprites["score_hitbox"].score = 0
this.run = true
this.diePlayed = false

}



this.onUpdate = function() {


	if(this.run == false) {
		this.x=0
		sprites["pipe"].x = -500
		sprites["pipe"].move = false
		if (!this.diePlayed) {
	this.playSound("die.ogg");
	this.diePlayed = true
}


	}
	if (this.run) {
		if (sprites["pipe"].x <= -75) {
			sprites["pipe"].show()
		}
	}

}

this.onStart = function() {
}


this.onUpdate = function() {
	if (this.isTouching("bird")) {
		sprites["play"].run = false

	}
	this.x = sprites["pipe"].x
	this.y = sprites["pipe"].y - this.height - 200


}

this.onStart = function() {
	this.move = false
	this.show = function(){
	this.y = this.getRandomInt(220,450)
	this.x = 370
	this.move = true
}
}



this.onUpdate = function() {
	if (this.isTouching("bird")) {
		sprites["play"].run = false

	}
	if (this.move) {
		this.x -= 2
	}


}

this.onStart = function() {
	this.speed = 1
	this.start_x = this.x
	this.c_code = `
this.onStart = function() {
	this.start_x = this.x
}

this.onUpdate = function() {
	this.x -= ${this.speed}
	if (this.x <= this.start_x - this.width) {
		this.x = this.start_x

	}
}

this.onClick = function() {
	sprites["bird"].y -= 50
}

`
this.x = this.start_x + this.width
this.clone(this.c_code)
this.x = this.start_x

}


this.onClick = function() {
	sprites["bird"].y -= 50
}

this.onUpdate = function() {
	this.x -= this.speed
	if (this.x <= this.start_x - this.width) {
		this.x = this.start_x

}

}

this.onStart = function() {

	this.score = 0
	this.used = false
}

this.onUpdate = function() {
	if (sprites["play"].run) {
		this.y = sprites["pipe"].y - 250
		this.x = sprites["pipe"].x + sprites["pipe"].width + 2
		if (this.used == false){
		if (this.isTouching("bird")) {
			this.used = true
			this.score += 1
			this.playSound("point.ogg")
			sprites["score"].set(this.score)
		}}
		if (this.x <= 20) {
			this.used = false
		}
	}

}

this.onStart = function() {
this.jumped = false;
this.playAnimation("fly",5)

}

this.onUpdate = function() {

	if (input.keydown[" "] && !this.jumped) {
	this.jumped = true
	this.y -= 50
	}
	if (input.keyup[" "] && this.jumped) {
		this.jumped = false
	}

	if (sprites["play"].run) {
		this.y += 3
	}
	if (this.isTouching(["base","base2"]).includes(true) || this.y <= 0) {

	sprites["play"].run = false

	}

	if (sprites["play"].run == false) {
	this.x= -500


}


}

this.onStart = function() {
	this.speed = 2
	this.start_x = this.x
	this.c_code = `
this.onStart = function() {
	this.start_x = this.x
}

this.onUpdate = function() {
	this.x -= ${this.speed}
	if (this.x <= this.start_x - this.width) {
		this.x = this.start_x

	}
}

`
this.x = this.start_x + this.width
this.clone(this.c_code,"base2")
this.x = this.start_x

}


this.onUpdate = function() {
	this.x -= this.speed
	if (this.x <= this.start_x - this.width) {
		this.x = this.start_x

}

}
